#include "graphics.h"


//Calculates the "map" with a visual representation of where the pack is on its journey home
void printDistance(gameState game)
{
	//How many feet/meters/whateverthefuckIdecidetheunitsare are represented by one character length?
	int nScale = 20;
	//Current location (distance already traveled), converted to character lengths
	int nLocationScaled = (con::nJourney - game.nDistance) / nScale;
	//Max journey, converted into character lengths
	int nMaxDistanceScaled = con::nJourney / nScale;

	//Prints to the terminal the current location in a scaled "map"
	std::cout << strBold("|", fmagenta);

	for (int i = 0; i < nMaxDistanceScaled; i++)
	{
		if (i == nLocationScaled)
		{
			std::cout << strBold("~", fmagenta);
		}
		else
		{
			std::cout << strBold("+", fmagenta);
		}
	}

	std::cout << strBold("|", fmagenta);
	std::cout << "\n";
}


